package main.java.ShayraAltamirano.LoopsPractice;

public class Loop3Ex {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=1;i<=10;i++) {
			if (i%2==0) {
				System.out.println("Even "+ i);
			}
			else {
				System.out.println("Odd " + i );
			}
		}
	}

}
